const CreatePokemon2 = () => {
    const [form, setForm] = useState({
        email: "",
        name: "",
        password: ""
    })


    const [count, setCount] = useState(1)


    const updateForm = (e) => {
        setForm({
            ...form,
            [e.target.name]: e.target.value
        })
    }



  return (
    <div>
        <form onSubmit={() => alert("Creado")}> 
        {count === 1 ? (
            <div>
                <label>Nombre</label>
                <input 
                type="text" 
                name="name"
                onChange={updateForm}
                value={form.name}
                />
            </div>
        ) : null}

        {count === 2 ? (
            <div>
                <label>Mail</label>
                <input 
                type="text" 
                name="email"
                onChange={updateForm}
                value={form.email}
                />

            </div> 
        ) : null}
        {count === 3 ? (
            <div>
                <label>Nombre</label>
                <input 
                type="text" 
                name="password"
                onChange={updateForm}
                value={form.password}
                />
            </div>
        ) : null}    

            <button type='submit'>Enviar</button>
        </form>
