import React, { useEffect, useState } from "react";
//import {useState, useEffect} from 'react';
import {useDispatch, useSelector} from 'react-redux'
import Maestro from "./Home/Maestro";



export default function Home(){

    const user = prompt("Ingresa tu nombre: ")

    const dispatch = useDispatch()
    const usuario = useSelector((state) => state.usuario[0].name)

    const [name, setName] = useState(usuario)

    console.log("usuario global",usuario)
    console.log(name)


    useEffect(() => {
        setName(JSON.parse(window.localStorage.getItem('name')))
    }, [])

    useEffect(() => {
        window.localStorage.setItem('name', name)
    }, [name])



    return(
        <div>
            <Maestro name={name} />
        </div>
    )
}
