import React, { useState } from 'react'

const CreatePokemon2 = () => {
    const [form, setForm] = useState({
        email: "",
        name: "",
        password: ""
    })


const [input, setInput] = useState({
    name: "",
    type: [],
    life: 0,
    attack: 0,
    defense: 0,
    velocity: 0,
    height: 0,
    weight: 0,
    Image: ""
})

    const [count, setCount] = useState(1)

    const updateInput = (e) => {
        setInput({
            ...input,
            [e.target.name]: e.target.value
        })
    }

    const updateForm = (e) => {
        setForm({
            ...form,
            [e.target.name]: e.target.value
        })
    }



  return (
    <div>
        <form onSubmit={() => alert("Creado")}> 
        {count === 1 ? (
            <div>
                <label>Nombre</label>
                <input 
                type="text" 
                name="name"
                onChange={(e) => updateInput(e)}
                value={input.name}
                />
            </div>
        ) : null}

        {count === 2 ? (
            <div>
                <label>Vida</label>
                <input 
                type="number" 
                name="life"
                onChange={(e) => updateInput(e)}
                value={input.life}
                />

            </div> 
        ) : null}
        {count === 3 ? (
            <div>
                <label>Altura</label>
                <input 
                type="number" 
                name="height"
                onChange={(e) => updateInput(e)}
                value={input.height}
                />
            </div>
        ) : null}
        {count === 4 ? (
            <div>
                <label>Peso</label>
                <input 
                type="number" 
                name="weight"
                onChange={(e) => updateInput(e)}
                value={input.weight}
                />
            </div>
        ) : null}
        {count === 5 ? (
            <div>
                <label>Peso</label>
                <input 
                type="number" 
                name="weight"
                onChange={(e) => updateInput(e)}
                value={input.weight}
                />
            </div>
        ) : null}          

            <button type='submit'>Enviar</button>
        </form>
        <button type='submit' onClick={() => setCount(count - 1)} disabled={count < 2}>Atr√°s</button>
        <button type='submit' onClick={() => setCount(count + 1)}>Siguiente</button>
    </div>
  )
}

export default CreatePokemon2
