import React, { useEffect, useState } from "react";
//import {useState, useEffect} from 'react';
import {useDispatch, useSelector} from 'react-redux'
import { getPokemons, getTypes, getUser } from "../actions";
import Maestro from "./Home/Maestro";



export default function Home(){
    const dispatch = useDispatch()
    const [name, setName] = useState('');

    let usuario = useSelector((state) => state.usuario[0]);


    useEffect(() => {
        dispatch(getPokemons())
        dispatch(getTypes())
    }, [dispatch])

    function handleOnChange(e) {
        e.preventDefault()
        setName(e.target.value)
        console.log(e.target.value)
    }

    function handleSubmit(e) {
        dispatch(getUser(name))
    }

    if(!usuario) {
        return (
            <div>
                <h1>¿Como desea ingresar a la Pokedex?</h1>
                <input type="text" onChange={(e) => handleOnChange(e)} />
                <button onClick={(e) => handleSubmit(e)}>Ingresar como maestro Pokemón</button>
                <button>Ingresar como invitado</button>
            </div>
        )
    } else {
        return (
            <div>
                <h2>Bienvenido maestro {usuario}</h2>
            </div>
        )
    }

    console.log(usuario)


    return(
        <div>
            <Maestro />
        </div>
    )
}
