import React, { useState, useEffect} from "react";
import { Link, useHistory } from 'react-router-dom';
import { postPokemon, getTypes } from "../actions";
import { useDispatch, useSelector } from 'react-redux';
import '../css/Form.css'




function validate(input) {
    let errors = {}
    if(!input.name) {
        errors.name = "Se requiere un nombre"
    } else if(!input.type.length < 1 || !input.type.length > 2){
        errors.type = "Se rquiere como mínimo 1 tipo y como máximo 2"
    }
    return errors;
}

export default function PokemonsCreate() {
    const dispatch = useDispatch()
    const types = useSelector((state) => state.types)
    const history = useHistory()
    const [errors, setErrors] = useState({})


        useEffect(() => {
            dispatch(getTypes())
        }, [dispatch])



    const [input, setInput] = useState({
        name: "",
        type: [],
        life: "",
        attack: "",
        defense: "",
        velocity: "",
        height: "",
        weight: "",
        image: ""
    })

    function handleOnChangue(e) {
        setInput({
            ...input,
            [e.target.name] : e.target.value
        })
        console.log(input)
    }


    function handleOnClick(e) {
        if(e.target.checked){
            setInput({
                ...input,
                status: e.target.value
            })
            setErrors(validate({
                ...input,
                [e.target.name]: e.target.value
            }))
        }
    }

    function handleSelect(e){
        setInput({
            ...input,
            type: [...input.type, e.target.value]
        })
    }

    function handleSubmit(e) {
        e.preventDefault()
        console.log(input)
        dispatch(postPokemon(input))
        alert("Pokemon creado")
        setInput({
        name: "",
        type: [],
        life: "",
        attack: "",
        defense: "",
        velocity: "",
        height: "",
        weight: "",
        image: ""
        })
        history.push('/home')
    }

    return (
        <div className = "form-box">
        <button>Regresar a Pokedex</button>
        <h5 className = "form-step"> Crea tu Pokemón </h5>
        <form onSubmit={(e) => handleSubmit(e)}>
            <div className = "field1">
            <input placeholder="Nombre:" type="text" value={input.name} name="name" onChange={(e) => handleOnChangue(e)}/>
            {
                errors.name && (
                    <p>{errors.name}</p>
                )
            }
            <input placeholder="Vida:" type="number" value={input.life} name="life" onChange={(e) => handleOnChangue(e)}/>            
            <input placeholder="Ataque:" type="number" value={input.attack} name="attack" onChange={(e) => handleOnChangue(e)}/>
            <input placeholder="Defensa:" type="number" value={input.defense} name="defense" onChange={(e) => handleOnChangue(e)}/>
            <input placeholder="Velocidad:" type="number" value={input.velocity} name="velocity" onChange={(e) => handleOnChangue(e)}/>
            <input placeholder="Altura:" type="number" value={input.height} name="height" onChange={(e) => handleOnChangue(e)}/>
            <input placeholder="Peso:" type="number" value={input.weight} name="weight" onChange={(e) => handleOnChangue(e)}/>
            <input placeholder="Imagen URL:" type="url" value={input.image} name="image" onChange={(e) => handleOnChangue(e)}/>
            </div>
            <select onChange={(e) => handleSelect(e)}>
                {
                    types?.map(el => {
                        return(
                            <option value={el.name}>{el.name}</option>
                        )
                    })
                }
            </select>
            <ul>
            <li>
            {input.type.map(el => el + "       ")
            }
            </li>
            </ul>
            <button type="submit" >Crear Pokemón</button>
        </form>
    </div>
    )
}
