import TYPES from "../actions/types";

const initialState = {
    pokemons: [],
    pokemonsCopy: [],
    types: [],
    usuario: [],
    pokedex: [],
    background: ""
}


function rootReducer (state=initialState, action) {
    switch(action.type){
        case TYPES.GET_POKEMONS:
            return {
                ...state,
                pokemons: action.payload,
                pokemonsCopy: action.payload
            }

        case TYPES.GET_TYPES:
            return {
                ...state,
                types: action.payload
            }

        case TYPES.GET_USER:
            return {
                ...state,
                usuario: action.payload
            }

        case TYPES.HANDLE_SORT:
            let sortedArray = action.payload  === 'AZ' ? state.pokemons.sort(function(a, b){
                if(a.name > b.name) {
                    return 1;
                }
                if(b.name > a.name) {
                    return -1
                }
                return 0
            }) : action.payload === 'ZA' ? state.pokemons.sort(function(a, b){
                if(b.name > a.name) {
                    return 1;
                }
                if(a.name > b.name) {
                    return -1
                }
                return 0
            }) : action.payload === "strong" ? state.pokemon.sort(function(a, b){
                return a.attack - b.attack
            }) : state.pokemons.sort(function(a, b) {
                return b.attack - a.attack
            })
            return{
                ...state,

            }



        default:
            return {
                state
            }
    }
}



export default rootReducer;
