import React, { useState } from 'react'


function validate(input) {
    let errors = {}
    if (input.name.length < 4 || input.name.length >10 || !input.name) {
        errors.name = "Se requiere un nombre, debe contener entre 4 y 10 caracteres"
    } 
    if (!input.type || input.type.length < 1 || input.type.length > 2) {
        errors.type = "Solo puedes agregar 1 o 2 tipos de pokemón"
    }
    if(!input.life || input.life < 20 || input.life > 150) {
        errors.life = "El mínimo de vida es de 20 y el máximo es de 150"
    } 
    if(!input.velocity || input.velocity < 10 || input.velocity > 100){
        errors.velocity = "La velocidad máxima es de 100 y la mínima de 10"
    } 
    if(!input.attack || input.attack < 30 || input.attack > 100){
        errors.attack = "El máximo de ataque es 100 y el mínimo de 30"
    }
     if(!input.weight || input.weight < 10 || input.weight > 200){
        errors.weight = "El mínimo de peso es 10 y el máximo es de 200"
    } 
    if(!input.height || input.height < 60 || input.height > 200){
        errors.height = "La altura mínima es de 60 y la máxima es de 200"
    } 
    if(input.image && input.image.length > 150){
        errors.image = "Solo se permiten 150 caracteres como máximo"
    }
    if(!input.defense || input.defense < 60 || input.defense > 200 ){
        errors.defense = "El mínimo de defensa es de 60 y el máximo es de 200"
    }
    return errors;
}


const CreatePokemon2 = () => {

    const [errors, setErrors] = useState({})


    const [form, setForm] = useState({
        email: "",
        name: "",
        password: ""
    })


const [input, setInput] = useState({
    name: "",
    type: [],
    life: 0,
    attack: 0,
    defense: 0,
    velocity: 0,
    height: 0,
    weight: 0,
    Image: ""
})

    const [count, setCount] = useState(1)

    const updateInput = (e) => {
        setInput({
            ...input,
            [e.target.name]: e.target.value
        })
        setErrors(validate({
            ...input,
            [e.target.name]: e.target.value
        }))
    }

    const updateForm = (e) => {
        setForm({
            ...form,
            [e.target.name]: e.target.value
        })
    }





  return (
    <div>
        <form onSubmit={() => alert("Creado")}> 
        {count === 1 ? (
            <div>
                <label>Nombre</label>
                <input
                type="text" 
                name="name"
                onChange={(e) => updateInput(e)}
                value={input.name}
                />
                {errors.name && (
                    <p>{errors.name}</p>
                )}
            </div>
        ) : null}

        {count === 2 ? (
            <div>
                <label>Vida</label>
                <input 
                type="number" 
                name="life"
                onChange={(e) => updateInput(e)}
                value={input.life}
                />

            </div> 
        ) : null}
        {count === 3 ? (
            <div>
                <label>Altura</label>
                <input 
                type="number" 
                name="height"
                onChange={(e) => updateInput(e)}
                value={input.height}
                />
            </div>
        ) : null}
        {count === 4 ? (
            <div>
                <label>Peso</label>
                <input 
                type="number" 
                name="weight"
                onChange={(e) => updateInput(e)}
                value={input.weight}
                />
            </div>
        ) : null}
        {count === 5 ? (
            <div>
                <label>Peso</label>
                <input 
                type="number" 
                name="weight"
                onChange={(e) => updateInput(e)}
                value={input.weight}
                />
            </div>
        ) : null}
        {count === 6 ? (
            <div>
                <label>Ataque</label>
                <input 
                type="number" 
                name="attack"
                onChange={(e) => updateInput(e)}
                value={input.attack}
                />
            </div>
        ) : null}
        {count === 7 ? (
            <div>
                <label>Defensa</label>
                <input 
                type="number" 
                name="defense"
                onChange={(e) => updateInput(e)}
                value={input.defense}
                />
            </div>
        ) : null}               
        {count === 8 ? (
            <div>
                <label>Velocidad</label>
                <input 
                type="number" 
                name="velocity"
                onChange={(e) => updateInput(e)}
                value={input.velocity}
                />
            </div>
        ) : null}       
            <button type='submit' disabled={
                input.name.length < 4 || 
                input.life < 20 || input.ilfe > 150 || 
                input.attack < 20 || input.attack > 200 || 
                input.defense < 30 || input.defense > 200 || 
                input.velocity < 10 || input.velocity > 150 || 
                input.height < 10 || input.height > 200 || 
                input.weight < 20 || input.weight > 180
                }>Enviar</button>
        </form>
        <button type='submit' onClick={() => setCount(count - 1)} disabled={count < 2 }>Atrás</button>
        <button type='submit' onClick={() => setCount(count + 1)} disabled={count === 8}>Siguiente</button>
    </div>
  )
}

export default CreatePokemon2
