import React from "react";
import {Link} from 'react-router-dom';
import {useState, useEffect} from 'react';
import {useDispatch, useSelector} from 'react-redux'
import { getPokemons, getTypes, getUser } from "../actions";

export default function LandingPage(){
    const [name, setName] = useState('');
    const dispatch = useDispatch();
    const allpokemons = useSelector((state) => state.pokemons)
    const allTypes = useSelector((state) => state.types)
    const user = useSelector((state) => state.user)

    useEffect(() => {
        dispatch(getPokemons())
        dispatch(getTypes())
    },[dispatch])


    function handleOnChange(e) {
        e.preventDefault()
        setName(e.target.value)
        console.log(e.target.value)
    }

    function handleSubmit(e) {
        e.preventDefault()
        dispatch(getUser(name))
    }

    return(
        <div>
            <h1>Bienvenidos a la Pokedex</h1>
            <div>
                <h4>Ingresar como Maestro Pokem√≥n</h4>
                <input type="text" onChange={(e) => handleOnChange(e)} />
                <button type="submit" onClick={(e) => handleSubmit(name)} >Bienvenido Maestro</button>
            </div>
            <Link to ='/home'>
                <button>Ingresar como invitado</button>
            </Link>
        </div>
    )
}
