import React, { useState, useEffect } from "react";
import { useHistory } from 'react-router-dom';
import { postPokemon, getTypes } from "../actions";
import { useDispatch, useSelector } from 'react-redux';
import '../css/create.css'




function validate(input) {
    let errors = {}
    if (input.name.length < 4 || input.name.length >10 || !input.name) {
        errors.name = "Se requiere un nombre, debe contener entre 4 y 10 caracteres"
    } 
    if (!input.type || input.type.length < 1 || input.type.length > 2) {
        errors.type = "Solo puedes agregar 1 o 2 tipos de pokemón"
    }
    if(!input.life || input.life < 20 || input.life > 150) {
        errors.life = "El mínimo de vida es de 20 y el máximo es de 150"
    } 
    if(!input.velocity || input.velocity < 10 || input.velocity > 100){
        errors.velocity = "La velocidad máxima es de 100 y la mínima de 10"
    } 
    if(!input.attack || input.attack < 30 || input.attack > 100){
        errors.attack = "El máximo de ataque es 100 y el mínimo de 30"
    }
     if(!input.weight || input.weight < 10 || input.weight > 200){
        errors.weight = "El mínimo de peso es 10 y el máximo es de 200"
    } 
    if(!input.height || input.height < 60 || input.height > 200){
        errors.height = "La altura mínima es de 60 y la máxima es de 200"
    } 
    if(input.image && input.image.length > 150){
        errors.image = "Solo se permiten 150 caracteres como máximo"
    }
    if(!input.defense || input.defense < 60 || input.defense > 200 ){
        errors.defense = "El mínimo de defensa es de 60 y el máximo es de 200"
    }
    return errors;
}

export default function PokemonsCreate() {

    function changingBackground (nombre) {
        document.body.className = nombre
    }

    changingBackground("create-pokemon")

    const dispatch = useDispatch()
    const types = useSelector((state) => state.types)
    const history = useHistory()
    const [errors, setErrors] = useState({})


    useEffect(() => {
        dispatch(getTypes())
    }, [dispatch])



    const [input, setInput] = useState({
        name: "",
        type: [],
        life: "",
        attack: "",
        defense: "",
        velocity: "",
        height: "",
        weight: "",
        image: ""
    })

    function handleOnChangue(e) {
        setInput({
            ...input,
            [e.target.name]: e.target.value
        })
        setErrors(validate({
            ...input,
            [e.target.name]: e.target.value
        }))
        console.log(errors)
    }


    function handleOnClick(e) {
        if (e.target.checked) {
            setInput({
                ...input,
                status: e.target.value
            })

        }
    }

    function handleSelect(e) {
        setInput({
            ...input,
            type: [...input.type, e.target.value]
        })
    }

    function handleSubmit(e) {
        e.preventDefault()
        console.log(input)
        dispatch(postPokemon(input))
        alert("Pokemon creado")
        setInput({
            name: "",
            type: [],
            life: "",
            attack: "",
            defense: "",
            velocity: "",
            height: "",
            weight: "",
            image: ""
        })
        history.push('/home')
    }

    return (
        <>
        <button className="booton">Regresar a Pokedex</button>
<form className="form-create" onSubmit={(e) => handleSubmit(e)}>

            <h4 className="h4-create"> Crea tu Pokemón </h4>
            <input className="controls-create" placeholder="Nombre:" type="text" value={input.name} name="name" onChange={(e) => handleOnChangue(e)} />
            {
                errors.name && (
                    <p className="errores">{errors.name}</p>
                )
            }
            <input className="controls-create" placeholder="Vida:" type="number" value={input.life} name="life" onChange={(e) => handleOnChangue(e)} />
            {
                errors.life && (
                    <p className="errores">{errors.life}</p>
                )
            }
            <input className="controls-create" placeholder="Ataque:" type="number" value={input.attack} name="attack" onChange={(e) => handleOnChangue(e)} />
            {
                errors.attack && (
                    <p className="errores">{errors.attack}</p>
                )
            }
            <input className="controls-create" placeholder="Defensa:" type="number" value={input.defense} name="defense" onChange={(e) => handleOnChangue(e)} />
            {
                errors.defense && (
                    <p className="errores">{errors.defense}</p>
                )
            }
            <input className="controls-create" placeholder="Velocidad:" type="number" value={input.velocity} name="velocity" onChange={(e) => handleOnChangue(e)} />
            {
                errors.velocity && (
                    <p className="errores">{errors.velocity}</p>
                )
            }
            <input className="controls-create" placeholder="Altura:" type="number" value={input.height} name="height" onChange={(e) => handleOnChangue(e)} />
            {
                errors.height && (
                    <p className="errores">{errors.height}</p>
                )
            }
            <input className="controls-create" placeholder="Peso:" type="number" value={input.weight} name="weight" onChange={(e) => handleOnChangue(e)} />
            {
                errors.weight && (
                    <p className="errores">{errors.weight}</p>
                )
            }
            <input className="controls-create" placeholder="Imagen URL:" type="url" value={input.image} name="image" onChange={(e) => handleOnChangue(e)} />
            {
                errors.image && (
                    <p className="errores">{errors.image}</p>
                )
            }
            <select className="selected" onChange={(e) => handleSelect(e)}>
                {
                    types?.map(el => {
                        return (
                            <option value={el.name} key={el.id}>{el.name}</option>
                        )
                    })
                }
            </select>
            <ul>
                <li>
                    {input.type.map(el => el + "")
                    }
                </li>
            </ul>

            {
                !errors.name && !errors.life && !errors.attack && !errors.defense && !errors.velocity && !errors.height && !errors.weight && !errors.image && (
                    <button className="booton" type="submit" >Crear Pokemón</button>
                )


            }

        </form>
        </>


    )
}
