import TYPES from "../actions/types";

const initialState = {
    pokemons: [],
    pokemonsCopy: [],
    pokemonsType: [],
    types: [],
    usuario: [],
    pokedex: [],
    detail: [],
    pokemonsSfilter: [] ///// ESTE ES PARA LOS DOBLES FILTRADOS
}


function rootReducer (state=initialState, action) {
    switch(action.type){

        case TYPES.GET_DETAIL:
            return {
                ...state,
                detail: action.payload 
            }

        case TYPES.GET_POKEMONS:
            return {
                ...state,
                pokemons: action.payload,
                pokemonsCopy: action.payload
            }

        case TYPES.GET_TYPES:
            return {
                ...state,
                types: action.payload
            }

        case TYPES.GET_USER:
            return {
                ...state,
                usuario: action.payload
            }

        case TYPES.HANDLE_SORT:
            let sortedArray = action.payload  === 'AZ' ? state.pokemons.sort(function(a, b){
                if(a.name > b.name) {
                    return 1;
                }
                if(b.name > a.name) {
                    return -1
                }
                return 0
            }) : action.payload === 'ZA' ? state.pokemons.sort(function(a, b){
                if(b.name > a.name) {
                    return 1;
                }
                if(a.name > b.name) {
                    return -1
                }
                return 0
            }) : action.payload === "strong" ? state.pokemons.sort(function(a, b){
                return a.attack - b.attack
            }) : state.pokemons.sort(function(a, b) {
                return b.attack - a.attack
            })
            return{
                ...state,
                pokemons: sortedArray
            }

///////////////////// ESTE FILTRADO YA FUNCIONA AL 100% SOLO         
        case TYPES.FILTERED_BY_TYPE:

        if(!state.pokemonsSfilter || state.pokemonsSfilter.length === 0){
            // console.log("Aquí entro")
            const allPokemonss = state.pokemonsCopy
            const statusFiltered = action.payload === "-" ? allPokemonss : allPokemonss.filter(el => el.type[0] === action.payload || el.type[1] === action.payload)
            return {
                ...state,
                pokemons: statusFiltered,
                pokemonsSfilter: statusFiltered
            }
        } else {
            const allPokemonss = state.pokemonsCopy
            const statusFiltered = action.payload === "-" ? allPokemonss : allPokemonss.filter(el => el.type[0] === action.payload || el.type[1] === action.payload)
            return {
                ...state,
                pokemons: statusFiltered,
                pokemonsSfilter: []
            }
        }

///////////////////////////////////////////////////////////////////

        case TYPES.FILTER_BY_CREATED:

            if(!state.pokemonsSfilter || state.pokemonsSfilter.length === 0){
                console.log("Entro aquí")
                const allPokemonss2 = state.pokemonsCopy
                const statusFiltered = action.payload === "-" ? allPokemonss2 : action.payload === "original" ? allPokemonss2.filter(el => !el.createdInDB) : allPokemonss2.filter(el => el.createdInDB)
                return {
                    ...state,
                    pokemons: statusFiltered,
                    pokemonsSfilter: statusFiltered
                }
            }




            // if(state.pokemonsType){
            //     const newFilt = state.pokemonsType
            //     const newFiltered = action.payload === "-" ? state.pokemonsType : action.payload === "original" ? newFilt.filter(el => !el.createdInDB) : newFilt.filter(el => el.createdInDB)
