            //         ...state,
            //         pokemons: newFiltered,
            //     }
            // } else {
            //     const allPokemons2 = state.pokemonsCopy
            //     const createdFiltered = action.payload === "-" ? state.pokemonsCopy : action.payload === "original" ? allPokemons2.filter(el => !el.createdInDB) : allPokemons2.filter(el => el.createdInDB)
            //     return {
            //         ...state,
            //         pokemons: createdFiltered,
            //         background: background
            //     }
            // }

        case TYPES.GET_NAME_POKEMON:
            return {
                ...state,
                pokemons: action.payload
            }

        case TYPES.SEARCH_LIVE:

            if(state.pokemonsType){
                let nuevoPoke = action.payload.length > 0 ? state.pokemonsType.filter(el => el.name.includes(action.payload.toLowerCase())) : state.pokemonsType

