                ...state,
                pokemons: nuevoPoke
            }
            } else {
                let nuevoPoke = action.payload.length > 0 ? state.pokemonsCopy.filter(el => el.name.includes(action.payload.toLowerCase())) : state.pokemonsCopy

            return {
                ...state,
                pokemons: nuevoPoke
            }
            }

        case TYPES.POST_POKEMON:
            return {
                ...state,
            }




        default:
            return {
                state
            }
    }
}



export default rootReducer;
