import TYPES from "../actions/types";

const initialState = {
    pokemons: [],
    pokemonsCopy: [],
    pokemonsType: [],
    types: [],
    usuario: [],
    pokedex: [],
    background: ""
}


function rootReducer (state=initialState, action) {
    switch(action.type){
        case TYPES.GET_POKEMONS:
            return {
                ...state,
                pokemons: action.payload,
                pokemonsCopy: action.payload
            }

        case TYPES.GET_TYPES:
            return {
                ...state,
                types: action.payload
            }

        case TYPES.GET_USER:
            return {
                ...state,
                usuario: action.payload
            }

        case TYPES.HANDLE_SORT:
            let sortedArray = action.payload  === 'AZ' ? state.pokemons.sort(function(a, b){
                if(a.name > b.name) {
                    return 1;
                }
                if(b.name > a.name) {
                    return -1
                }
                return 0
            }) : action.payload === 'ZA' ? state.pokemons.sort(function(a, b){
                if(b.name > a.name) {
                    return 1;
                }
                if(a.name > b.name) {
                    return -1
                }
                return 0
            }) : action.payload === "strong" ? state.pokemons.sort(function(a, b){
                return a.attack - b.attack
            }) : state.pokemons.sort(function(a, b) {
                return b.attack - a.attack
            })
            return{
                ...state,
                pokemons: sortedArray
            }


        case TYPES.FILTERED_BY_TYPE:
            const pokemonsCopy = state.pokemonsCopy
            const statusFiltered = action.payload === "-" ? pokemonsCopy : pokemonsCopy.filter(el => el.type[0] === action.payload || el.type[1] === action.payload)
            return {
                ...state,
                pokemons: statusFiltered,
                pokemonsType: statusFiltered
            }

        case TYPES.FILTER_BY_CREATED:
            if(state.pokemonsType){
                const newFilt = state.pokemonsType
                const newFiltered = action.payload === "-" ? state.pokemonsCopy : action.payload === "original" ? newFilt.filter(el => !el.createdInDB) : newFilt.filter(el => el.createdInDB)
                return {
                    ...state,
                    pokemons: newFiltered
                }
            } else {
                const allPokemons2 = state.pokemonsCopy
                const createdFiltered = action.payload === "-" ? state.pokemonsCopy : action.payload === "original" ? allPokemons2.filter(el => !el.createdInDB) : allPokemons2.filter(el => el.createdInDB)
                return {
                    ...state,
                    pokemons: createdFiltered
                }
            }    

        default:
            return {
                state
            }
    }
}



export default rootReducer;
