import React, { useState } from 'react'

const CreatePokemon2 = () => {
    const [form, setForm] = useState({
        email: "",
        name: "",
        password: ""
    })


const [input, setInput] = useState({
    name: "",
    type: [],
    life: 0,
    attack: 0,
    defense: 0,
    velocity: 0,
    height: 0,
    weight: 0,
    Image: ""
})

    const [count, setCount] = useState(1)

    const updateInput = (e) => {
        setInput({
            ...input,
            [e.target.name]: e.target.value
        })
    }

    const updateForm = (e) => {
        setForm({
            ...form,
            [e.target.name]: e.target.value
        })
    }


    const [validate, setValidate] = useState(false)



  return (
    <div>
        <form onSubmit={() => alert("Creado")}> 
        {count === 1 ? (
            <div>
                <label>Nombre</label>
                <input required
                type="text" 
                name="name"
                onChange={(e) => updateInput(e)}
                value={input.name}
                />
            </div>
        ) : null}

        {count === 2 ? (
            <div>
                <label>Vida</label>
                <input 
                type="number" 
                name="life"
                onChange={(e) => updateInput(e)}
                value={input.life}
                />

            </div> 
        ) : null}
        {count === 3 ? (
            <div>
                <label>Altura</label>
                <input 
                type="number" 
                name="height"
                onChange={(e) => updateInput(e)}
                value={input.height}
                />
            </div>
        ) : null}
        {count === 4 ? (
            <div>
                <label>Peso</label>
                <input 
                type="number" 
                name="weight"
                onChange={(e) => updateInput(e)}
                value={input.weight}
                />
            </div>
        ) : null}
        {count === 5 ? (
            <div>
                <label>Peso</label>
                <input 
                type="number" 
                name="weight"
                onChange={(e) => updateInput(e)}
                value={input.weight}
                />
            </div>
        ) : null}
        {count === 6 ? (
            <div>
                <label>Ataque</label>
                <input 
                type="number" 
                name="attack"
                onChange={(e) => updateInput(e)}
                value={input.attack}
                />
            </div>
        ) : null}
        {count === 7 ? (
            <div>
                <label>Defensa</label>
                <input 
                type="number" 
                name="defense"
                onChange={(e) => updateInput(e)}
                value={input.defense}
                />
            </div>
        ) : null}               
        {count === 8 ? (
            <div>
                <label>Velocidad</label>
                <input 
                type="number" 
                name="velocity"
                onChange={(e) => updateInput(e)}
                value={input.velocity}
                />
            </div>
        ) : null}       
            <button type='submit' disabled={
                input.name.length < 4 || 
                input.life < 20 || input.ilfe > 150 || 
                input.attack < 20 || input.attack > 200 || 
                input.defense < 30 || input.defense > 200 || 
                input.velocity < 10 || input.velocity > 150 || 
                input.height < 10 || input.height > 200 || 
                input.weight < 20 || input.weight > 180
                }>Enviar</button>
        </form>
        <button type='submit' onClick={() => setCount(count - 1)} disabled={count < 2 }>Atr√°s</button>
        <button type='submit' onClick={() => setCount(count + 1)} disabled={count === 8}>Siguiente</button>
    </div>
  )
}

export default CreatePokemon2
