import React, { useEffect, useState } from "react";
//import {useState, useEffect} from 'react';
import { useDispatch, useSelector } from 'react-redux'
import { Link } from "react-router-dom";
import { getPokemons, getTypes, getUser } from "../actions";
import Card from "./Card";
import Maestro from "./Home/Maestro";
import Paginado from "./Paginado";
import '../css/card.css'
import '../css/home.css'
import Filtros from "./Filtros";



export default function Home() {


    //ESTADO LOCAL PARA CAMBIAR LOS FONDOS CUANDO SE ACTUALICE

    const [didUpdate, setDidUpdate] = useState("general")
    useEffect(() => {
        if (didUpdate === "general") {
            document.body.className = "home1"
        } 
    })

    //TERMINA ESTADO LOCAL PARA CAMBIAR LOS FONDOS DE PATALLA 


    const dispatch = useDispatch()
    var [name, setName] = useState('');
    let user = ""


    const allPokemons = useSelector((state) => state.pokemons)

    const [currentPage, setCurrentPage] = useState(1)
    const [pokemonsPerPage,] = useState(12)
    const indexOfLastPokemon = currentPage * pokemonsPerPage
    const indexOfFirstPokemon = indexOfLastPokemon - pokemonsPerPage
    const currentPokemons = allPokemons?.slice(indexOfFirstPokemon, indexOfLastPokemon)


    const paginado = (PageNumber) => {
        setCurrentPage(PageNumber)
    }


    useEffect(() => {
        dispatch(getPokemons())
        dispatch(getTypes())
    }, [dispatch])

    function handleOnChange(e) {
        e.preventDefault()
        user = e.target.value
    }

    function handleSubmit(e) {
        dispatch(getUser(user))
        setName(user)
    }



    // useEffect(() => {
    //     document.body.className = "home1"
    // }, [])

    if (!name) {
        return (
            <div className="maestroPokemon">
                <h1>Ingresa tu nombre maestro Pokem√≥n</h1>
  {/*//////////////////////////////////    ELEMENTO INPUT  //////////////////////// */}             
            <form className="formNombre" action={(e) => handleSubmit(e)}>
            <div className="input-cont">
                <div className="iinput">
                    <input className="iinput2" type="text" required />
                    <label className="labelinput">Nombre</label>
                </div>
            </div>

            </form>
            <button onClick={(e) => handleSubmit(e)}>Entrar</button>

  {/*//////////////////////////////////    ELEMENTO INPUT  //////////////////////// */}  



            </div>
        )
    } else {
        return (
            <div className="maestroPokemon">
                <h2>Bienvenido a tu POKEDEX maestro {name.toUpperCase()}</h2>
                <Paginado pokemonsPerPage={pokemonsPerPage} allPokemons={allPokemons?.length} paginado={paginado} />
                <Filtros />
                {
                    currentPokemons?.map((p) => {
                        return (
                            <div key={p.id} className="bckgrndImg">
                                <Link to={"/pokemons/" + p.id}  >
                                    <Card name={p.name} image={p.image} type={p.type} key={p.id} />
                                </Link>
                            </div>
                        )
                    })
                }
            </div>
        )
    }


}
