import React, { useEffect, useState } from "react";
//import {useState, useEffect} from 'react';
import { useDispatch, useSelector } from 'react-redux'
import { Link } from "react-router-dom";
import { getPokemons, getTypes, getUser, orderByName, filterPokemonsByType, filterCreated } from "../actions";
import Card from "./Card";
import Paginado from "./Paginado";
import '../css/card.css'
import '../css/home.css'
import '../css/home2.css'
import Filtros from "./Filtros";
import SearchBar from "./SearchBar";






export default function Home() {
    let nameFromLocalStorage = JSON.parse(sessionStorage.getItem('name2' || undefined));  //= JSON.parse(localStorage.getItem('name2') || [])


    const [name2, setName2] = useState(nameFromLocalStorage)

    sessionStorage.setItem('name2', JSON.stringify(name2))







    const dispatch = useDispatch()
    var [name, setName] = useState('');


    let user = ""


    const allPokemons = useSelector((state) => state.pokemons)
    const [orden, setOrden] = useState('')
    const [currentPage, setCurrentPage] = useState(1)
    const [pokemonsPerPage,] = useState(12)
    const indexOfLastPokemon = currentPage * pokemonsPerPage
    const indexOfFirstPokemon = indexOfLastPokemon - pokemonsPerPage
    const currentPokemons = allPokemons?.slice(indexOfFirstPokemon, indexOfLastPokemon)


//##################### CAMBIO DE BACKGROUND





    const paginado = (PageNumber) => {
        setCurrentPage(PageNumber)
    }


    useEffect(() => {
        dispatch(getPokemons())
        dispatch(getTypes())
        changingBackground("general")
    }, [dispatch])

    function handleOnChange(e) {
        e.preventDefault()
        user = e.target.value
    }

    function handleSubmit(e) {
        dispatch(getUser(user))
        setName(user)
        setName2(user)
    }

    function handleSort(e) {
        console.log(orden)
        e.preventDefault()
        dispatch(orderByName(e.target.value))
        setCurrentPage(1)
        setOrden(`Ordenado ${e.target.value}`)
    }

    function changingBackground (nombre) {
        document.body.className = nombre
    }


    function handleFilteredByType(e) {
        dispatch(filterPokemonsByType(e.target.value))
        setCurrentPage(1)
        console.log(e.target.value)
        changingBackground(e.target.value)
    }


    function handleFilteredByCreated (e) {
        dispatch(filterCreated(e.target.value))
        setCurrentPage(1)

    }




    if (!name) {

        if(nameFromLocalStorage){
            user = nameFromLocalStorage
            dispatch(getUser(user))
            setName(user)
        } else {
            return (
                <div className="maestroPokemon">
                    <h1>Ingresa tu nombre maestro Pokem√≥n</h1>
      {/*//////////////////////////////////    ELEMENTO INPUT  //////////////////////// */}             
                <form className="formNombre" >
                <div className="input-cont">
                    <div className="iinput" >
                        <input className="iinput2" type="text" onChange={(e) => handleOnChange(e)}/>
                        <label className="labelinput">Nombre</label>
                    </div>
                </div>

                </form>

                <button onClick={(e) => handleSubmit(e)}>Entrar</button>
      {/*//////////////////////////////////    ELEMENTO INPUT  //////////////////////// */}  





                </div>
            )
        }


    } else {
        return (
            <div className="maestroPokemon">
                <h2>Bienvenido a tu POKEDEX maestro {name.toUpperCase()}</h2>
                <Paginado pokemonsPerPage={pokemonsPerPage} allPokemons={allPokemons?.length} paginado={paginado} />

                <div >
                    <SearchBar setCurrentPage={setCurrentPage}/>
                </div>

                <Filtros filtros = {{handleSort, handleFilteredByType, handleFilteredByCreated}}/>
                {
                    currentPokemons?.map((p) => {
                        return (
                            <div key={p.id} className="bckgrndImg">
                                <Link to={"/pokemons/" + p.id}  >
                                    <Card name={p.name} image={p.image} type={p.type} key={p.id} />
                                </Link>
                            </div>
                        )
                    })
                }
            </div>
        )
    }


}
