import React, { useEffect, useState } from "react";
//import {useState, useEffect} from 'react';
import {useDispatch, useSelector} from 'react-redux'
import { getPokemons, getTypes, getUser } from "../actions";
import Maestro from "./Home/Maestro";



export default function Home(){
    const dispatch = useDispatch()
    const [name, setName] = useState('');
    console.log(name)
    let user = ""


    useEffect(() => {
        dispatch(getPokemons())
        dispatch(getTypes())
    }, [dispatch])

    function handleOnChange(e) {
        e.preventDefault()
        user = e.target.value
        console.log(e.target.value)
    }

    function handleSubmit(e) {
        dispatch(getUser(user))
        setName(user)
    }

    if(!name) {
        console.log(name)
        return (
            <div>
                <h1>¿Como desea ingresar a la Pokedex?</h1>
                <input type="text" onChange={(e) => handleOnChange(e)} />
                <button onClick={(e) => handleSubmit(e)}>Ingresar como maestro Pokemón</button>
                <button>Ingresar como invitado</button>
            </div>
        )
    } else {
        return (
            <div>
                <h2>Bienvenido maestro {name}</h2>
            </div>
        )
    }




    return(
        <div>
            <Maestro />
        </div>
    )
}
