import React, { useEffect, useState } from 'react'
import { Link, useHistory } from 'react-router-dom';
import { postPokemon, getTypes } from "../actions";
import { useDispatch, useSelector } from 'react-redux';
import '../css/form.css'



function validate(input) {
    let errors = {}
    if(!input.name) {
        errors.name = "Se requiere un nombre"
    } else if(!input.type.length < 1 || !input.type.length > 2){
        errors.type = "Se rquiere como mínimo 1 tipo y como máximo 2"
    }
    return errors;
}




const CreatePokemon = () => {
    const dispatch = useDispatch()
    const types = useSelector((state) => state.types)
    const history = useHistory()
    const [errors, setErrors] = useState({})

    useEffect(() => {
        dispatch(getTypes())
    }, [dispatch])


    const [input, setInput] = useState({
        name: "",
        type: [],
        life: "",
        attack: "",
        defense: "",
        velocity: "",
        height: "",
        weight: "",
        image: ""
    })

    function handleOnChangue(e) {
        setInput({
            ...input,
            [e.target.name] : e.target.value
        })
        console.log(input)
    }


    function handleOnClick(e) {
        if(e.target.checked){
            setInput({
                ...input,
                status: e.target.value
            })
            setErrors(validate({
                ...input,
                [e.target.name]: e.target.value
            }))
        }
    }


    function handleSelect(e){
        setInput({
            ...input,
            type: [...input.type, e.target.value]
        })
    }


    function handleSubmit(e) {
        e.preventDefault()
        console.log(input)
        dispatch(postPokemon(input))
        alert("Pokemon creado")
        setInput({
        name: "",
        type: [],
        life: "",
        attack: "",
        defense: "",
        velocity: "",
        height: "",
        weight: "",
        image: ""
        })
        history.push('/home')
    }


  return (
    <div className="container-form">
    <div className="title-form">Crea un Pokemón</div>
    <div className="content-form">
      <form onSubmit={(e) => handleSubmit(e)}>
        <div className="user-details-form">
          <div className="input-box-form">
            <span className="details-form">Nombre</span>
            <input type="text" placeholder="Mínimo 4 caractéres" value={input.name} required name="name" />
            {
                errors.name && (
                    <p>{errors.name}</p>
                )
            }
          </div>
          <div className="input-box-form">
            <span className="details-form" value={input.life} name="life">Vida</span>
            <input type="number" placeholder="Entre 1 y 100"  />
          </div>
          <div className="input-box-form">
            <span className="details-form" value={input.defense} name="defense">Defensa</span>
            <input type="number" placeholder="Entre 1 y 100"  />
          </div>
          <div className="input-box-form">
            <span className="details-form" value={input.attack} name="attack">Ataque</span>
            <input type="number" placeholder="Entre 1 y 100"  />
          </div>
          <div className="input-box-form">
            <span className="details-form">Velocidad</span>
            <input type="number" placeholder="Entre 1 y 100" value={input.velocity} name="velocity" />
          </div>
          <div className="input-box-form">
            <span className="details-form" value={input.height} name="height">Altura</span>
            <input type="number" placeholder="Entre 1 y 200"  />
          </div>
          <div className="input-box-form">
            <span className="details-form" value={input.weight} name="weight">Peso: </span>
            <input type="number" placeholder="Entre 1 y 100"  />
          </div>
          <div className="input-box-form">
            <span className="details-form">Imagen URL</span>
            <input type="url" value={input.image} placeholder="URL corta"  name="image" />
          </div>
          <div className="input-box-form">
          </div>
          <div>
          <select onChange={(e) => handleSelect(e)}>
                {
                    types?.map(el => {
                        return(
                            <option value={el.name}>{el.name}</option>
                        )
                    })
                }
            </select>
            <ul><li>{input.type.map(el => el + "       ")}</li></ul>
            <button type="submit" >Crear Pokemón</button>
          </div>
        </div>

        <div className="button-form">
          <input type="submit" value="Crear" />
        </div>
      </form>
    </div>
  </div>
  )
}

export default CreatePokemon
