import React from "react";
import { Link } from 'react-router-dom';
import { useDispatch, useSelector } from "react-redux";
import { getDetail } from "../actions";
import { useEffect } from "react";

export default function Detail(props) {
    const dispatch = useDispatch()



    let id = props.match.params.id



    useEffect(() => {
        dispatch(getDetail(id))
    }, [dispatch])



    const myPokemon = useSelector((state) => state.detail)
    const pokemon = myPokemon? myPokemon[0] : console.log(myPokemon)  


    return (
        <div>
            {
                pokemon? 
                <div>
                <h2>{pokemon.name}</h2>
                <img src={pokemon.image} />
                <h3>Estadisticas de Vida</h3>
                <h4>Pokemon numero: {pokemon.id}</h4>
                <h4>Vida: {pokemon.life}</h4>
                <h4>Tipo: {pokemon.type.length === 1 ? <h4>{pokemon.type[0]}</h4> :<><h4>{pokemon.type[0]}</h4> <h4>{pokemon.type[1]}</h4></> }</h4>
                <h3>Estadisticas de combate: </h3>
                <h4>Defensa: {pokemon.defense}</h4>
                <h4>Ataque: {pokemon.attack}</h4>
                <h4>Velocidad: {pokemon.velocity}</h4>
                <h4>Peso: {pokemon.weight}</h4>
                <h4>Altura: {pokemon.height}</h4>
                </div> : <h2>Cargando...</h2>
            }
        </div>
    )
}


